update.foreach:
UPDATE item_image
SET file_name = #{image.fileName}
WHERE uuid = #{image.uuid} AND item_id = #{itemId}

mapper.update:
UPDATE item
SET is_disabled = 1
WHERE item_id = #{itemId}

mapper.select:
SELECT uuid, item_id as itemId, file_name as fileName
FROM item_image
WHERE item_id = #{itemId}

mapper.update:
UPDATE category
SET category_name = #{categoryName}, parent_id = #{parentId}
WHERE category_id = #{categoryId}

mapper.select:
SELECT i.item_id, i.item_name, i.price,
im.uuid, im.file_name
FROM item i
LEFT JOIN item_image im ON i.item_id = im.item_id
WHERE i.brand = #{brand}
AND i.category_id = #{categoryId}
AND i.item_id != #{excludeItemId}
AND i.is_disabled = 0
ORDER BY i.item_id
LIMIT 5
